    public static ArrayList<List_Wisata> getListData(){
        ArrayList<List_Wisata> list = new ArrayList<>();
        for (int i = 0; i <namaWisata.length; i++){
            List_Wisata listMovie = new List_Wisata();
            listMovie.setName(namaWisata[i]);
            listMovie.setLokasi(lokasiWIsata[i]);
            listMovie.setRating(ratingWisata[i]);
            listMovie.setDetail(deskWisata[i]);
            listMovie.setPhoto(imageWisata[i]);
            list.add(listMovie);
        }
        return list;
    };
